<?php/** * Created by PhpStorm. * User: dingo * Date: 13/04/2019 * Time: 02:23 */namespace App\Models;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;use function Qiniu\json_decode;use function Qiniu\time;class FeedbackModel extends Model{    use SoftDeletes;    protected $table = "tz_feedback";    public $primaryKey = 'id';    protected $dates = ['deleted_at'];    public $timestamps = true;    public static $status = [0 => '未读', 1 => '已读', 2 => '已处理'];    /**     * 修改时间是时间戳格式     */    public function fromDateTime($value){        return strtotime(parent::fromDateTime($value));    }    /**     * 关联用户     * @author duo_mi     * @date 2019-04-14     */    public function user()    {        return $this->hasOne(TzUserModel::class, 'id');    }    /**     * 创建问题     */    public static function createQ($user_id, $describe, $contact){        return self::insert([            'describe' => $describe,            'user_id' => $user_id,            'contact' => $contact,            'created_at' => \time(),        ]);    }}